<?php

namespace zhtSDK\Library\RegularExpression
    {
    class zhtSDK extends \Illuminate\Routing\Controller
        {
        /*
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Method Name     : __construct                                                                                          |
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Version         : 1.0000.0000000                                                                                       |
        | ▪ Last Update     : 2022-01-31                                                                                           |
        | ▪ Description     : System's Default Constructor                                                                         |
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Input Variable  :                                                                                                      |
        |      ▪ (mixed)  varUserSession ► User Session                                                                            |
        | ▪ Output Variable :                                                                                                      |
        |      ▪ (void)                                                                                                            |
        +--------------------------------------------------------------------------------------------------------------------------+
        */
        public function __construct($varUserSession)
            {
            }


        /*
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Method Name     : getRegEx_General_Date                                                                                |
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Version         : 1.0000.0000000                                                                                       |
        | ▪ Last Update     : 2022-01-31                                                                                           |
        | ▪ Description     : Mendapatkan Pattern Regular Expression Date                                                          |
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Input Variable  :                                                                                                      |
        |      ▪ (void)                                                                                                            |
        | ▪ Output Variable :                                                                                                      |
        |      ▪ (string) varReturn                                                                                                |
        +--------------------------------------------------------------------------------------------------------------------------+
        */ 
        public function getRegEx_General_Date()
            {
            $varReturn = 
                "/^".
                "(([0-9]{4})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1]))".
                "$/";
            return $varReturn;
            }


        /*
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Method Name     : getRegEx_General_TimeStamp                                                                           |
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Version         : 1.0000.0000000                                                                                       |
        | ▪ Last Update     : 2022-01-31                                                                                           |
        | ▪ Description     : Mendapatkan Pattern Regular Expression TimeStamp                                                     |
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Input Variable  :                                                                                                      |
        |      ▪ (void)                                                                                                            |
        | ▪ Output Variable :                                                                                                      |
        |      ▪ (string) varReturn                                                                                                |
        +--------------------------------------------------------------------------------------------------------------------------+
        */ 
        public function getRegEx_General_TimeStamp()
            {
            $varReturn = 
                "/^".
                "(([0-9]{4})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1]))".
                " ".
                "(".
                    "([0-1][0-9]|2[0-3]):([0-5][0-9]):([0-5][0-9])".
                    "((.[0-9]{1,6}){0,1})".
                    "(".
                        "([+]|[-])".
                        "(([0-1][0-9]|2[0-3]):([0-5][0-9]))".
                    "){0,1}".
                ")".
                "$/";
            return $varReturn;
            }


        /*
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Method Name     : getRegEx_JSON_TimeStamp                                                                              |
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Version         : 1.0000.0000000                                                                                       |
        | ▪ Last Update     : 2022-01-31                                                                                           |
        | ▪ Description     : Mendapatkan Pattern Regular Expression TimeStamp JSON                                                |
        +--------------------------------------------------------------------------------------------------------------------------+
        | ▪ Input Variable  :                                                                                                      |
        |      ▪ (void)                                                                                                            |
        | ▪ Output Variable :                                                                                                      |
        |      ▪ (string) varReturn                                                                                                |
        +--------------------------------------------------------------------------------------------------------------------------+
        */ 
        public function getRegEx_JSON_TimeStamp()
            {
            $varReturn = 
                "/^".
                "(([0-9]{4})-(0[1-9]|1[0-2])-(0[1-9]|[1-2][0-9]|3[0-1]))".
                "T".
                "(".
                    "([0-1][0-9]|2[0-3]):([0-5][0-9]):([0-5][0-9])".
                    "((.[0-9]{1,6}){0,1})".
                    "(".
                        "([+]|[-])".
                        "(([0-1][0-9]|2[0-3]):([0-5][0-9]))".
                    "){0,1}".
                ")".
                "$/";
            return $varReturn;
            }


        }
    }